import{_ as p,c as o,a as t,b as a,t as r,d as i,o as s}from"./app.ff46b2ef.js";const f=JSON.parse('{"title":"터미널 사용법","description":"","frontmatter":{"title":"터미널 사용법"},"headers":[],"relativePath":"server/터미널 사용법.md","lastUpdated":null}'),n={name:"server/터미널 사용법.md"},d={id:"frontmatter-title",tabindex:"-1"},c=t("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),_=i("<p>프로그램 위치 아는법</p><p>엔진엑스 경로 보기</p><p>which nginx 하면 경로가 나온다.</p><p>cd = 해당 경로로 이동 합니다. - cd /&lt;경로명&gt;</p><p>pwd = 현제 경로를 표시합니다.</p><p>ls = 조건 검색어 - ls st 파일 이름 중간에 st 를 포함한 모든 파일을 표시합니다.</p><p>cp = 원본복사 - cp web /bin &quot;web&quot;폴더를 /bin에 복사합니다.</p><p>mv = 원본이동 - mv web /bin &quot;web&quot;폴더를 /bin으로 이동합니다.</p><p>rm = 원복삭제 - rm web or rm -r web &quot;web&quot;폴더를 삭제 합니다.</p><p>mkdir = 폴더 생성 - mkdir web &quot;web&quot; 폴더를 현제 경로에 생성합니다.</p><p>rmdir = 폴더삭제 - rmdir web &quot;web&quot; 폴더를 현제 경로에서 삭제합니다.</p><p>touch = 파일생성 - touch web &quot;web&quot;의 무용량 파일을 생성 합니다.</p><p>grep = 파일안 내용찾기 - grep head index.php &quot;index.php&quot; 에서 head 가 포함된 낱말을 찾어 냅니다.</p><p>ln = 링크 - ln -s /bin/web/ apath main &quot;/bin/web/&quot; 의 해당경로를 apath main 으로 링크합니다.</p><p>who = 현제 접속 또는 로그인중인 모든 사용자를 찾어줍니다.</p><p>ps = ps(옵션) - 현제 실행중인 모든 프로세서 표시합니다.</p><p>kill = kill ichat - ichat 프로세서를 강제 종료 합니다.</p><p>find = 조건검색어 - find /root/ -name password 파일이름에 password 를 포함한 파일을 /root 폴더에서 검색합니다.</p><p>whereis = whereis ichat &quot;ichat 가 있는 프로세서 경로를 표시합니다.</p><p>sudo = sudo vi /etc/php.ini root 권한으로 /etc/ 폴더에 php.ini 파일을 vi로 편집합니다.</p>",20);function h(e,u,l,b,m,w){return s(),o("div",null,[t("h1",d,[a(r(e.$frontmatter.title)+" ",1),c]),_])}const v=p(n,[["render",h]]);export{f as __pageData,v as default};
